(193, 1, '2025-04-03 14:01:13', '2025-04-03 12:01:13', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"fr\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Contactez-nous</title> <!-- Nouveau titre -->\n    <link href=\"https://fonts.googleapis.com/css2?family=Astro&display=swap\" rel=\"stylesheet\"> <!-- Police Astrome -->\n    <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css\"> <!-- Font Awesome -->\n    <style>\n        body {\n            font-family: \'Astro\', sans-serif;\n            background: linear-gradient(135deg, #f0f1f6, #e2e8f0);\n            margin: 0;\n            padding: 0;\n            color: #333;\n        }\n\n        header {\n            background: linear-gradient(90deg, #3498db, #8e44ad);\n            padding: 30px;\n            text-align: center;\n            color: white;\n            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);\n        }\n\n        header h1 {\n            margin: 0;\n            font-size: 40px;\n        }\n\n        .container {\n            width: 90%;\n            margin: 30px auto;\n            max-width: 1200px;\n        }\n\n        .contact-info, .contact-form {\n            background-color: white;\n            padding: 30px;\n            border-radius: 10px;\n            box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);\n            margin-bottom: 30px;\n        }\n\n        .contact-info h2, .contact-form h2 {\n            font-size: 28px;\n            color: #2c3e50;\n            margin-bottom: 20px;\n        }\n\n        .contact-info p, .contact-info a {\n            font-size: 18px;\n            color: #555;\n            margin: 10px 0;\n        }\n\n        .contact-info a {\n            text-decoration: none;\n            color: #3498db;\n        }\n\n        .contact-info a:hover {\n            text-decoration: underline;\n        }\n\n        .contact-info .fas {\n            color: #3498db;\n            margin-right: 10px;\n            font-size: 20px;\n        }\n\n        .contact-form input, .contact-form textarea {\n            width: 100%;\n            padding: 15px;\n            margin-bottom: 20px;\n            border: 2px solid #ccc;\n            border-radius: 8px;\n            font-size: 16px;\n            transition: border-color 0.3s ease;\n        }\n\n        .contact-form input:focus, .contact-form textarea:focus {\n            border-color: #3498db;\n            outline: none;\n        }\n\n        .contact-form button {\n            background-color: #8e44ad;\n            color: white;\n            padding: 15px 30px;\n            border: none;\n            border-radius: 8px;\n            cursor: pointer;\n            font-size: 18px;\n            transition: background-color 0.3s ease;\n        }\n\n        .contact-form button:hover {\n            background-color: #9b59b6;\n        }\n\n        @media (max-width: 768px) {\n            .container {\n                width: 95%;\n            }\n\n            header h1 {\n                font-size: 32px;\n            }\n\n            .contact-info h2, .contact-form h2 {\n                font-size: 24px;\n            }\n\n            .contact-form input, .contact-form textarea {\n                font-size: 14px;\n            }\n\n            .contact-form button {\n                font-size: 16px;\n            }\n        }\n    </style>\n</head>\n<body>\n\n<header>\n    <h1>Contactez-nous</h1> <!-- Nouveau titre -->\n</header>\n\n<div class=\"container\">\n    <div class=\"contact-info\">\n        <h2>Informations de Contact</h2>\n        <p><i class=\"fas fa-envelope\"></i><strong>Email :</strong> <a href=\"mailto:soniaghnimi100@gmail.com\">soniaghnimi100@gmail.com</a></p> <!-- Correction du lien mail -->\n        <p><i class=\"fas fa-phone-alt\"></i><strong>Téléphone :</strong> +216 27350855</p>\n        <p><i class=\"fas fa-map-marker-alt\"></i><strong>Adresse :</strong> Rue Lmatar 0.5 km, Sfax</p>\n    </div>\n\n    <div class=\"contact-form\">\n        <h2>Formulaire de Contact</h2>\n        <form action=\"mailto:soniaghnimi100@gmail.com\" method=\"post\" enctype=\"text/plain\">\n            <label for=\"name\">Nom :</label>\n            <input type=\"text\" id=\"name\" name=\"name\" placeholder=\"Votre nom\" required>\n\n            <label for=\"email\">Email :</label>\n            <input type=\"email\" id=\"email\" name=\"email\" placeholder=\"Votre email\" required>\n\n            <label for=\"message\">Message :</label>\n            <textarea id=\"message\" name=\"message\" rows=\"4\" placeholder=\"Votre message\" required></textarea>\n\n            <button type=\"submit\">Envoyer</button>\n        </form>\n    </div>\n</div>\n\n</body>\n</html>\n\n<!-- /wp:html -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Contactez Nous', '', 'publish', 'closed', 'closed', '', 'contactez-nous', '', '', '2025-04-04 15:51:00', '2025-04-04 13:51:00', '', 0, 'http://localhost/wordpress/?page_id=193', 0, 'page', '', 0),
